import Vue from 'vue'
import VueRouter from 'vue-router'
import Layout from '../views/Layout.vue'
import Cookies from "js-cookie";

Vue.use(VueRouter)

const routes = [
  //  ====== 登录  =====
  {
    path: '/login',
    name: 'Login',
    component: () => import('@/views/login/Login.vue'),
  },
  //  ====== 注册  =====
  {
    path: '/register',
    name: 'Register',
    component: () => import('@/views/register/Register.vue'),
  },
    //  ====== 主页  =====
  {
    path: '/',
    name: 'Layout',
    component: Layout,
    redirect: '/home',
    children: [  // 子路由
      {
        path: 'home',
        name: 'Home',
        component: () => import('@/views/boxModel/BoxModelLayout.vue'),
      },
      //  ====  Fluid Layout  ====
      {
        path: 'fluidLayout',
        name: 'FluidLayout',
        component: () => import('@/views/fluid/FluidLayout.vue'),
      },
      //  ====  Flexbox Layout  ====
      {
        path: 'flexboxLayout',
        name: 'FlexboxLayout',
        component: () => import('@/views/flexbox/FlexboxLayout.vue'),
      },
      //  ====  Grid Layout  ====
      { 
        path: 'gridLayout', 
        name: 'GridLayout', 
        component: () => import('@/views/grid/GridLayout.vue') 
      },
      //  ====  Positioning Layout ====
      { 
        path: 'positionLayout', 
        name: 'PositioningLayout', 
        component: () => import('@/views/position/PositioningLayout.vue') 
      },

      //  ====  Multi-Column Layout  ====

      { 
        path: 'multiColumnLayout', 
        name: 'MultiColumnLayout', 
        component: () => import('@/views/multiColumn/MultiColumnLayout') },

      //  ====  Responsive Layout  ====
      { 
        path: 'responsiveLayout', 
        name: 'ResponsiveLayout', 
        component: () => import('@/views/responsive/ResponsiveLayout.vue') },
    ]
  },
  {
    path: "*",
    component:() => import('@/views/404')
  }
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

router.beforeEach((to, from, next) => {
  if (to.path === '/login' || to.path === '/register') next()
  const admin = Cookies.get("admin")
  if (!admin && to.path !== '/login' && to.path !== '/register') return next("/login")  // 强制退回到登录页面
  // 访问 /home 的时候，并且cookie里面存在数据，这个时候我就直接放行
  next()
})

export default router
