import Vue from 'vue'
import VueRouter from 'vue-router'
import Layout from '../views/Layout.vue'
import Cookies from "js-cookie";

Vue.use(VueRouter)

const routes = [
  //  ====== 登录  =====
  {
    path: '/login',
    name: 'Login',
    component: () => import('@/views/login/Login.vue'),
  },
  //  ====== 注册  =====
  {
    path: '/register',
    name: 'Register',
    component: () => import('@/views/register/Register.vue'),
  },
    //  ====== 主页  =====
  {
    path: '/',
    name: 'Layout',
    component: Layout,
    redirect: '/home',
    children: [  // 子路由
      {
        path: 'home',
        name: 'Home',
        component: () => import('@/views/home/HomeView.vue'),
      },
      //  ====  FluidLayout  ====
      {
        path: 'fluidLayout',
        name: 'FluidLayout',
        component: () => import('@/views/fluid/FluidLayout.vue'),
      },
      //  ====  FlexboxLayout  ====
      {
        path: 'flexboxLayout',
        name: 'FlexboxLayout',
        component: () => import('@/views/admin/List.vue'),
      },
      //  ====  GridLayout  ====
      { 
        path: 'gridLayout', 
        name: 'GridLayout', 
        component: () => import('@/views/category/Add.vue') 
      },
      //  ====  Book  ====

      { path: 'addBook', name: 'AddBook', component: () => import('@/views/book/Add.vue') },

      //  ====  Borrow  ====

      { path: 'addBorrow', name: 'AddBorrow', component: () => import('@/views/borrow/Add.vue') },

      //  ====  Return  ====
      { path: 'returList', name: 'returList', component: () => import('@/views/retur/List.vue') },
    ]
  },
  {
    path: "*",
    component:() => import('@/views/404')
  }
]

const router = new VueRouter({
  mode: 'history',
  base: process.env.BASE_URL,
  routes
})

router.beforeEach((to, from, next) => {
  if (to.path === '/login' || to.path === '/register') next()
  const admin = Cookies.get("admin")
  if (!admin && to.path !== '/login' && to.path !== '/register') return next("/login")  // 强制退回到登录页面
  // 访问 /home 的时候，并且cookie里面存在数据，这个时候我就直接放行
  next()
})

export default router
